import "../css/Social.css";
import Post from "./Post";
import ImgUpload from "./ImgUpload";
import logo from "../images/pawprint_logo.png";
import cat_img from "../images/social/powell_cat_image.jpeg";
import { NavLink } from "react-router-dom";
import React, {useEffect, useState } from "react";
import Axios from 'axios';

function Social() {
  const [uploads, setUploads] = useState([]);
  const user = localStorage.getItem("username");

  useEffect(() => {
    if (!localStorage.getItem("loggedIn")) {
      localStorage.setItem("loggedIn", false);
    }
  }, []);

  useEffect(() => {
    Axios.get("http://localhost:8000/social").then((response) => {
      setUploads(response.data);
    });
  }, []);


  const likePost = (id, key) => {
    if (localStorage.getItem("loggedIn") === "false"){
      alert("please log in to like!")
    }
    else if (likeStatus(user,id) === "true"){
      Axios.post("http://localhost:8000/unlike", {
        user_id: user,
        post_id: id,
      }).then((response) => {

        alert("you have unliked this post")
      });
    }
    else{
      var tempLikes = uploads;
      tempLikes[key].likes = tempLikes[key].likes + 1;
      Axios.post("http://localhost:8000/like", {
        user_id: localStorage.getItem("username"),
        post_id: id,
      }).then((response) => {
        setUploads(tempLikes);
        alert("you have liked this post")
      });
    }

  };

  return (
    <div>
  {uploads.map((val, key) => {
    return (
      <div className="App">
        <select name="pets" id="pet-select">
          <option value="">--Please choose an option--</option>
          <option value="dog">Dog</option>
          <option value="cat">Cat</option>
          <option value="squirrel">Squirrel</option>
          <option value="bird">Bird</option>
          <option value="bunny">Bunny</option>
          <option value="others">Others</option>
        </select>
        <button className="select">select</button>
        <div className="postbutton">
          <NavLink class="nav-link" to="/create">
            <button>Post</button>
          </NavLink>
        </div>
        <ul>
          <li>
            <Post/>
          </li>
        </ul>
      </div>
    );
  })}
</div>
)
}

export default Social;
